const mongoose = require('mongoose');

// The unique ID for a Post will be MongoDB's default _id, autogenerated.
const postSchema = mongoose.Schema({
  sub: Number, // Unique ID of the Sub to which the Post belongs.
  title: String, // Title of the Post.
  type: String, // e.g. Video, Image (e.g. an IMGUR link), Text
  text: String, // Body of the Post, if applicable.
  author: Number, // Unique ID of the User to which this Post belongs.
  date: {
    type: Date,
    default: Date.now,
  }, // Self-explanatory.
});
